-- server.luaprint("server code begin ---")local srv = net.createServer(net.TCP, 30)local function onReceive(sck, payload)    local _, indexEnd = string.find(payload, '"coffee":"')    local coffee = string.sub(payload,indexEnd + 1, indexEnd + 1)    print("Selected coffee", coffee)    if(coffee == "1")then        activateServo(2)    elseif(coffee == "2")then        activateServo(3)    elseif(coffee == "3")then        activateServo(4)    elseif(coffee == "4")then        activateServo(5)    elseif(coffee == "5")then        activateServo(6)    end    print("Reply status 200")    sck:send("HTTP/1.1 200 OK\r\n\r\n", function(sck) sck:close() end)endif srv then    print("start listening on port 80...")    srv:listen(80, function(conn)        conn:on("receive", onReceive)    end)endprint("server code end ---")